mapper.select:
select a.id, a.account_id, a.username, a.country_region_code, a.mobile, a.email, a.nickname, a.avatar, a.gender, a.reg_source, a.enable, a.row_valid, a.description, a.create_time, a.create_by, a.modify_time, a.modify_by from db_account.tb_account a where a.row_valid = 1
AND a.account_id = #{accountId}
AND a.username like concat('%', #{username}, '%')
AND a.mobile like concat('%', #{mobile}, '%')
AND a.enable = #{enable}
AND date_format(a.create_time,'%Y%m%d') >= date_format(#{params.beginTime},'%Y%m%d')
AND date_format(a.create_time,'%Y%m%d') <= date_format(#{params.endTime},'%Y%m%d')
${params.dataScope}

select distinct u.user_id, u.dept_id, u.login_name, u.user_name, u.user_type, u.email, u.avatar, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.login_name like concat('%', #{loginName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

mapper.delete:
update sys_user
set del_flag = '2'
where user_id = #{userId}

mapper.update:
update sys_user
dept_id = #{deptId}, login_name = #{loginName}, user_name = #{userName}, user_type = #{userType}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, salt = #{salt}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, pwd_update_date = #{pwdUpdateDate}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where user_id = #{userId}
